import React,{useState} from 'react'
import Todo from './Todo'

function TodosList() {

    const [todos,setTodos] = useState([
        {
            id:1,
            title:'titre 1',
            project: 'Project 1'
        }
    ])
    const [title, setTitle] = useState('')
    const [project, setProject] = useState('')
    const handleSubmit = (e)=>{
        e.preventDefault()
        console.log(e.target.value);
    }
    return (
       <div  className='container'>
        <div className='col-12 '>
            <form className='mt-4 ' onSubmit={handleSubmit}>
                <div className='card card-body text-white text-start  bg-dark'>
                    <div className='form-group'>
                        <label>Title</label>
                        <input className='form-control' value={title} onChange={(e)=>{setTitle(e.target.value)}}  type="text" />
                        <label>Projet</label>
                        <input className='form-control'  type="text" value={project} onChange={(e)=>{setProject(e.target.value)}}/>
                        <button className='btn btn-primary mt-4' type="submit" >Create</button>
                        <button className='btn btn-danger mt-4 mx-4' >Cancel</button>
                    </div>
                </div>
            </form>
        </div>
       {
        todos.map(todo =>{
            return(
                <Todo
                key={todo.id}
                id={todo.id}
                title={todo.title}
                projet = {todo.project}
                />
            )
        })
       }

       </div>
    )
}

export default TodosList
